const menuBtn=document.getElementById("menubutton"),menu=document.getElementById("primary-menu");menuBtn.addEventListener("click",openMenu);function openMenu(){menu.removeAttribute("hidden"),menuBtn.setAttribute("aria-expanded",!0)}function closeMenu(){menu.setAttribute("hidden",!0),menuBtn.setAttribute("aria-expanded",!1)}window.addEventListener("click",function(e){menu.contains(e.target)||menuBtn.contains(e.target)?console.log("click inside menu"):(console.log("click outside menu"),closeMenu())});function toggleDropdown(e){e.preventDefault();const t=e.target.nextElementSibling;t.style.display=t.style.display==="block"?"none":"block"}document.addEventListener("click",function(e){const t=e.target.closest(".LangNav");if(!t){const e=document.querySelectorAll(".dropdown-content");e.forEach(e=>e.style.display="none")}});function cleanForm(){document.getElementById("contact-form").reset()}